INSERT INTO tree_type (id, name, description) VALUES (1, 'Дуб', 'Дуб — рід багаторічних рослин родини букових, що налічує приблизно 430 негібридних видів. Його представники поширені переважно у помірних і тропічних областях Північної півкулі. Серед дубів існують цінні деревні, танідоносні, лікарські та декоративні рослини, деякі з них у минулому широко використовували як харчові.');
INSERT INTO tree_type (id, name, description) VALUES (2, 'Клен', 'Клен — рід дерев та кущів родини сапіндових. Налічує близько 150 видів, батьківщина більшості з яких — Азія, але кілька видів також мешкають в Європі, Північній Африці та Північній Америці. Клени — медоноси');
INSERT INTO tree_type (id, name, description) VALUES (3, 'Липа', 'Ли́па — рід дерев родини мальвових. Налічує близько 30 видів. Поширений у зоні помірного клімату в Північній півкулі.');
INSERT INTO tree_type (id, name, description) VALUES (4, 'Кедр', 'Кедр — рід класу хвойних родини соснових, найбільш пов''язаний з ялицею. Головними районами поширення кедру є Середземномор''я та Гімалаї.');
INSERT INTO tree_type (id, name, description) VALUES (5, 'Сосна', 'Со́сна́ — рід хвойних дерев родини соснових. Це дерева або чагарники, ароматичні, вічнозелені; крона зазвичай конічна у молодих рослин, часто стає округлою або плосковерхою з віком.');
INSERT INTO tree_type (id, name, description) VALUES (6, 'Ялина', 'Яли́на, смерека (Picea) — рід хвойних дерев родини соснових, поширених від субтропіків на великій висоті до помірних і холодних районів Євразії й Північної Америки. Найпівденніший вид, Picea morrisonicola живе на Тайвані на 23° пн. широти, на висотах 2500–3000 метрів.');
INSERT INTO tree_type (id, name, description) VALUES (7, 'Каштан', 'Кашта́н — рід рослин родини букових. Це дерева заввишки до 35 м або чагарники, інколи карликові. Об''єднує вісім близьких між собою видів, поширених у країнах Середземномор''я, Гімалаях, у Північній Америці, на Кавказі. Один вид зростає на Чорноморському узбережжі Кавказу і в Закавказзі, а в Україні культивується.');
INSERT INTO tree_type (id, name, description) VALUES (8, 'Бук', 'Бук — рід дерев родини букових заввишки 20—40 м. У цьому роді налічується 9—10 видів.');
INSERT INTO tree_type (id, name, description) VALUES (9, 'Граб', 'Граб — рід багаторічних деревних рослин родини березових, до складу якого входить 41 вид. Існують варіанти родової назви, наприклад, Carpiniphyllum. Серед представників роду багато декоративних, лісомеліоративних, технічних культур, деякі з них мають обмежене значення як кормові рослини.');
INSERT INTO tree_type (id, name, description) VALUES (10, 'Тополя', 'Тополя — рід деревних листопадних рослин родини вербових, заввишки 18 — 45 м і більше.');
INSERT INTO tree_type (id, name, description) VALUES (11, 'Верба', 'Верба́ — рід дерев, кущів або напівкущів родини вербових. Деякі види рослин з роду верба мають і деревну, і кущову форми.');
